mapper.select:
SELECT a.ad_idx, a.item_cd, a.item_name, FORMAT(a.ad_price, 0) AS ad_price, a.ad_place, FORMAT(a.ad_cnt, 0) AS ad_cnt, FORMAT(a.ad_real_cnt, 0) AS ad_real_cnt, FORMAT(a.ad_adj_cnt, 0) AS ad_adj_cnt, a.ad_uom, DATE_FORMAT(a.ad_reg_dte, '%Y-%m-%d %H:%i') AS ad_reg_dte FROM sm_adjust a WHERE 1=1
AND a.item_name LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY ad_idx DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT pd_tank FROM sm_plc_data
WHERE pd_type = #{loType}
GROUP BY pd_tank

mapper.update:
UPDATE sm_item SET
	item_stock = #{adRealCnt},
	item_per_price = #{adPrice},
	item_tank = #{adPlace}
WHERE item_cd = #{itemCd}
