mapper.select:
SELECT COUNT(*) FROM sm_reversal a JOIN sm_insp_change b ON a.WO_IDX = b.WO_IDX JOIN sm_work_order c ON a.WO_IDX = c.WO_IDX JOIN sm_orders d ON c.OR_IDX = d.OR_IDX JOIN sm_estimate e ON d.ES_IDX = e.ES_IDX JOIN sm_account f ON e.A_IDX = f.AC_CODE JOIN sm_item g ON c.ITEM_CD = g.ITEM_CD
AND wo_name LIKE CONCAT('%',#{searchKeyword},'%')
AND di_reg_dte >= #{searchStDate}
AND di_reg_dte <= #{searchEdDate}

SELECT a.re_idx, c.wo_name, f.ac_name, g.item_name, DATE_FORMAT(a.DI_REG_DTE, '%Y-%m-%d %H:%i') AS di_reg_dte, a.re_cnt FROM sm_reversal a JOIN sm_insp_change b ON a.WO_IDX = b.WO_IDX JOIN sm_work_order c ON a.WO_IDX = c.WO_IDX JOIN sm_orders d ON c.OR_IDX = d.OR_IDX JOIN sm_estimate e ON d.ES_IDX = e.ES_IDX JOIN sm_account f ON e.A_IDX = f.AC_CODE JOIN sm_item g ON c.ITEM_CD = g.ITEM_CD
AND wo_name LIKE CONCAT('%',#{searchKeyword},'%')
AND di_reg_dte >= #{searchStDate}
AND di_reg_dte <= #{searchEdDate}
ORDER BY re_idx DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) FROM
sm_insp_change
WHERE wo_idx = #{woIdx}

mapper.update:
UPDATE sm_reversal SET
RE_MANAGER = #{reManager},
DI_EDT_DTE = NOW(),
DI_EDT_MEM = #{userId}
WHERE re_idx = #{reIdx}

UPDATE sm_item SET
item_stock = (item_stock + ${cnt})
WHERE item_cd = #{itemCd}
