mapper.select:
SELECT COUNT(*) FROM sm_plc_data
WHERE 1=1

	AND pd_tank = #{searchKeyword}


	AND pd_st_dte >= #{searchStDate}


	AND pd_st_dte <=
	#{searchEdDate}

SELECT pd_idx, pd_tank, pd_material, pd_setting, pd_act_val, pd_status, date_format(pd_reg_dte, '%Y-%m-%d %H:%i') AS pd_reg_dte FROM sm_plc_data WHERE 1=1
AND pd_tank LIKE CONCAT('%',#{searchKeyword},'%')
AND pd_st_dte >= #{searchStDate}
AND pd_st_dte <= #{searchEdDate}
ORDER BY pd_idx DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
