mapper.select:
SELECT COUNT(*) FROM sm_analysis_info
WHERE 1=1

	AND ti_name LIKE CONCAT('%',#{searchKeyword},'%')


	AND ti_dte >= #{searchStDate}


	AND ti_dte <=
	#{searchEdDate}

SELECT ti_idx, do_idx, ti_name, ti_analyst, ti_dte, ti_state, wo_idx FROM sm_analysis_info WHERE 1=1
AND ti_name LIKE CONCAT('%',#{searchKeyword},'%')
AND ti_dte >= #{searchStDate}
AND ti_dte <= #{searchEdDate}
ORDER BY ti_idx DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT b.or_state FROM sm_work_order a JOIN
sm_orders b ON a.OR_IDX=b.OR_IDX
WHERE a.wo_idx = #{woIdx}

mapper.update:
UPDATE sm_document SET
do_state = #{state}
WHERE do_idx
= #{doIdx}
