mapper.select:
SELECT COUNT(*) FROM sm_analysis_info
WHERE 1=1

	AND ti_name LIKE CONCAT('%',#{searchKeyword},'%')


	AND ti_dte >= #{searchStDate}


	AND ti_dte <=
	#{searchEdDate}

SELECT ti_idx, do_idx, ti_name, ti_analyst, ti_dte, ti_state FROM sm_analysis_info WHERE 1=1
AND ti_name LIKE CONCAT('%',#{searchKeyword},'%')
AND ti_dte >= #{searchStDate}
AND ti_dte <= #{searchEdDate}
ORDER BY ti_idx DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT ti_idx, wo_idx, az_idx, ti_state, ti_analyst, date_format(ti_reg_dte, '%Y-%m-%d %H:%i') AS ti_reg_dte FROM
sm_analysis_info
WHERE ti_idx = #{tiIdx}

mapper.update:
UPDATE sm_document SET
do_state = #{state}
WHERE do_idx
= #{doIdx}
