mapper.select:
SELECT COUNT(*) FROM sm_delivery a JOIN sm_orders b ON a.or_idx = b.or_idx WHERE 1=1
AND or_name LIKE CONCAT('%', #{searchKeyword}, '%')
AND de_dte >= #{searchStDate}
AND de_dte <= #{searchEdDate}

SELECT a.de_idx, a.or_idx, a.de_chk_manager, a.de_dte, a.de_state, b.or_name FROM sm_delivery a JOIN sm_orders b ON a.or_idx = b.or_idx WHERE 1=1
AND or_name LIKE CONCAT('%', #{searchKeyword}, '%')
AND de_dte >= #{searchStDate}
AND de_dte <= #{searchEdDate}
ORDER BY de_idx DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
UPDATE sm_orders SET
	or_state = #{state}
WHERE or_idx = #{orIdx}
