mapper.select:
SELECT or_idx, es_idx, or_name, or_dte, FORMAT(or_money,0) AS or_money, or_manager FROM sm_orders WHERE 1=1
AND or_name LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY or_idx DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
UPDATE sm_estimate SET
	es_state = #{state}
WHERE es_idx = #{esIdx}
